#!/usr/bin/env node

const http = require('http');

// Fetch and execute the bundled JS
function testApp() {
  // First get the HTML
  http.get('http://localhost:8080/app-test.html', (res) => {
    let html = '';
    res.on('data', chunk => html += chunk);
    res.on('end', () => {
      console.log('✓ HTML loaded');

      // Now get the JS bundle
      http.get('http://localhost:8080/dist/index.js', (res) => {
        let js = '';
        res.on('data', chunk => js += chunk);
        res.on('end', () => {
          console.log('✓ JS bundle loaded (' + js.length + ' bytes)');

          // Check if main function exists
          if (js.includes('exports["main"]') || js.includes('Main.main')) {
            console.log('✓ Main function found in bundle');
          } else {
            console.log('✗ Main function not found in bundle');
          }

          // Check for Halogen
          if (js.includes('Halogen') || js.includes('runUI')) {
            console.log('✓ Halogen code found in bundle');
          } else {
            console.log('✗ Halogen code not found');
          }

          // Check for our App component
          if (js.includes('Component.App') || js.includes('ListConversations')) {
            console.log('✓ App component found in bundle');
          } else {
            console.log('✗ App component not found');
          }

          // Test API endpoint
          http.get('http://localhost:8000/v1/conversations', (res) => {
            let data = '';
            res.on('data', chunk => data += chunk);
            res.on('end', () => {
              try {
                const json = JSON.parse(data);
                console.log('✓ API returned ' + json.data.length + ' conversations');

                // Check conversation types
                const types = {};
                json.data.forEach(c => {
                  types[c.type] = (types[c.type] || 0) + 1;
                });
                console.log('  Types:', Object.keys(types).join(', '));
              } catch (e) {
                console.log('✗ API error:', e.message);
              }
            });
          }).on('error', e => {
            console.log('✗ Cannot reach backend at :8000');
          });
        });
      }).on('error', e => {
        console.log('✗ Cannot load JS bundle');
      });
    });
  }).on('error', e => {
    console.log('✗ Cannot reach frontend at :8080');
  });
}

testApp();

