const { test, expect } = require('@playwright/test');

test.describe('PureScript Frontend', () => {
  test('should load and mount the app', async ({ page }) => {
    await page.goto('/');

    // Wait for app to mount (should replace loading message)
    await expect(page.locator('#app')).not.toContainText('Loading PureScript app...');

    // Check that the app has mounted something
    const appChildren = await page.locator('#app > *').count();
    expect(appChildren).toBeGreaterThan(0);

    // Check console for successful startup
    const consoleMessages = [];
    page.on('console', msg => consoleMessages.push(msg.text()));

    // Reload to capture console logs
    await page.reload();
    await page.waitForTimeout(500);

    const hasStartupMessage = consoleMessages.some(msg => 
      msg.includes('PureScript app starting')
    );
    expect(hasStartupMessage).toBeTruthy();
  });

  test('should fetch and display conversations', async ({ page }) => {
    await page.goto('/');

    // Wait for the app to mount
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Check for conversation list or loading state
    const hasConversations = await page.locator('.conversation-item').count() > 0 ||
                             await page.locator('text=/Loading conversations/i').isVisible() ||
                             await page.locator('text=/No conversations/i').isVisible();

    expect(hasConversations).toBeTruthy();
  });

  test('should connect to WebSocket', async ({ page }) => {
    const wsMessages = [];

    // Monitor WebSocket connections
    page.on('websocket', ws => {
      console.log(`WebSocket opened: ${ws.url()}`);
      ws.on('framesent', frame => wsMessages.push({ type: 'sent', payload: frame.payload }));
      ws.on('framereceived', frame => wsMessages.push({ type: 'received', payload: frame.payload }));
    });

    await page.goto('/');
    await page.waitForTimeout(2000); // Give WebSocket time to connect

    // Check if any WebSocket activity occurred
    console.log('WebSocket messages:', wsMessages.length);

    // The app should at least attempt to connect
    const wsConnections = await page.evaluate(() => {
      return window.performance.getEntriesByType('resource')
        .filter(entry => entry.name.includes('ws://') || entry.name.includes('wss://'))
        .length;
    });

    // Either we see WebSocket frames or at least an attempt to connect
    expect(wsMessages.length > 0 || wsConnections > 0).toBeTruthy();
  });

  test('should handle conversation type mapping correctly', async ({ page }) => {
    // This tests that wiki_tag gets mapped to Wiki properly
    await page.goto('/');

    // Wait for app to load
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Check that there are no console errors about unknown conversation types
    const consoleErrors = [];
    page.on('console', msg => {
      if (msg.type() === 'error') {
        consoleErrors.push(msg.text());
      }
    });

    await page.reload();
    await page.waitForTimeout(1000);

    const hasTypeError = consoleErrors.some(err => 
      err.includes('Unknown conversation type') || 
      err.includes('wiki_tag')
    );

    expect(hasTypeError).toBeFalsy();
  });
});

