const { test, expect } = require('@playwright/test');

test.describe('Conversation Management', () => {
  test('should display list of conversations', async ({ page }) => {
    await page.goto('/');
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Should show conversations or empty state
    const hasConversationUI = 
      await page.locator('.conversation-list, .conversations, [data-conversations]').isVisible() ||
      await page.locator('text=/conversation/i').isVisible();

    expect(hasConversationUI).toBeTruthy();
  });

  test('should handle chat and wiki conversation types', async ({ page }) => {
    await page.goto('/');
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Check that both types can be displayed without errors
    const consoleErrors = [];
    page.on('console', msg => {
      if (msg.type() === 'error' && msg.text().includes('conversation type')) {
        consoleErrors.push(msg.text());
      }
    });

    await page.waitForTimeout(1000);
    expect(consoleErrors.length).toBe(0);
  });

  test('should select a conversation', async ({ page }) => {
    await page.goto('/');
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Try to click on a conversation
    const convItem = page.locator('.conversation-item, [data-conversation-id]').first();
    if (await convItem.isVisible()) {
      await convItem.click();

      // Should show messages or empty message list
      const hasMessageArea = 
        await page.locator('.messages, .message-list, [data-messages]').isVisible() ||
        await page.locator('text=/message/i').isVisible();

      expect(hasMessageArea).toBeTruthy();
    }
  });

  test('should send a message in a conversation', async ({ page }) => {
    await page.goto('/');
    await page.waitForSelector('#app > div', { timeout: 5000 });

    // Select a conversation if available
    const convItem = page.locator('.conversation-item, [data-conversation-id]').first();
    if (await convItem.isVisible()) {
      await convItem.click();
      await page.waitForTimeout(500);

      // Find message input
      const messageInput = page.locator('input[type="text"], textarea, [contenteditable="true"]').first();
      if (await messageInput.isVisible()) {
        const testMessage = `Test message ${Date.now()}`;
        await messageInput.fill(testMessage);

        // Send message (Enter or button)
        const sendButton = page.locator('button:has-text("Send")');
        if (await sendButton.isVisible()) {
          await sendButton.click();
        } else {
          await messageInput.press('Enter');
        }

        // Wait for message to appear
        await page.waitForTimeout(1000);

        // Check if message was added to the UI
        const messageAppeared = await page.locator(`text=/${testMessage}/`).isVisible();
        expect(messageAppeared).toBeTruthy();
      }
    }
  });

  test('should handle API errors gracefully', async ({ page }) => {
    // Test with backend down
    await page.route('**/api/**', route => route.abort());
    await page.route('**/v1/**', route => route.abort());

    await page.goto('/');
    await page.waitForTimeout(2000);

    // Should show error state or retry, not crash
    const hasErrorHandling = 
      await page.locator('text=/error/i').isVisible() ||
      await page.locator('text=/retry/i').isVisible() ||
      await page.locator('text=/offline/i').isVisible() ||
      await page.locator('text=/loading/i').isVisible();

    // App should still be mounted
    const appChildren = await page.locator('#app > *').count();
    expect(appChildren).toBeGreaterThan(0);
  });
});

