import { useState, useEffect, useCallback } from 'react';
import { Conversation, Message } from '../types';
import { apiClient } from '../services/api';

export function useConversation(conversationId: string | null) {
  const [conversation, setConversation] = useState<Conversation | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!conversationId) {
      setLoading(false);
      return;
    }

    let unsubscribe: (() => void) | null = null;

    const loadConversation = async () => {
      try {
        setLoading(true);
        setError(null);
        const conv = await apiClient.getConversation(conversationId);
        setConversation(conv);

        // Connect WebSocket for real-time updates
        apiClient.connectWebSocket(conversationId);

        // Subscribe to new messages
        unsubscribe = apiClient.onMessage((message: Message) => {
          setConversation((prev) => {
            if (!prev) return prev;
            return {
              ...prev,
              messages: [...prev.messages, message],
            };
          });
        });
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load conversation');
      } finally {
        setLoading(false);
      }
    };

    loadConversation();

    return () => {
      if (unsubscribe) {
        unsubscribe();
      }
      apiClient.disconnect();
    };
  }, [conversationId]);

  const sendMessage = useCallback(
    async (authorId: string, content: string) => {
      if (!conversationId || !conversation) return;

      try {
        const message = await apiClient.sendMessage(conversationId, authorId, content);
        // Optimistically update local state
        setConversation((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            messages: [...prev.messages, message],
          };
        });
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to send message');
      }
    },
    [conversationId, conversation]
  );

  const updateMessage = useCallback(
    async (messageId: string, content: string) => {
      if (!conversationId || !conversation) return;

      try {
        const updatedMessage = await apiClient.updateMessage(
          conversationId,
          messageId,
          content
        );
        // Update local state
        setConversation((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            messages: prev.messages.map((msg) =>
              msg.id === messageId ? updatedMessage : msg
            ),
          };
        });
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to update message');
      }
    },
    [conversationId, conversation]
  );

  return {
    conversation,
    loading,
    error,
    sendMessage,
    updateMessage,
  };
}

