import { StatusBar } from 'expo-status-bar';
import { StyleSheet, View, Text, ActivityIndicator } from 'react-native';
import { useState, useEffect } from 'react';
import ChatInterface from './src/components/ChatInterface';
import { useConversation } from './src/hooks/useConversation';
import { apiClient } from './src/services/api';

export default function App() {
  const [conversationId, setConversationId] = useState<string | null>(null);
  const [initializing, setInitializing] = useState(true);
  const { conversation, loading, error, sendMessage, updateMessage } = useConversation(conversationId);

  useEffect(() => {
    // Create or load a default conversation on app start
    const initializeApp = async () => {
      try {
        // Try to get existing conversations first
        const conversations = await apiClient.getConversations();

        if (conversations.length > 0) {
          // Use the first conversation
          setConversationId(conversations[0].id);
        } else {
          // Create a new conversation
          const newConv = await apiClient.createConversation(
            'chat',
            'Main Chat',
            [
              { id: 'user-1', type: 'human', name: 'You' },
              { id: 'ai-1', type: 'ai', name: 'Assistant' },
            ]
          );
          setConversationId(newConv.id);
        }
      } catch (err) {
        console.error('Failed to initialize:', err);
      } finally {
        setInitializing(false);
      }
    };

    initializeApp();
  }, []);

  if (initializing || loading) {
    return (
      <View style={[styles.container, styles.centered]}>
        <ActivityIndicator size="large" />
        <Text style={styles.loadingText}>Loading conversation...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={[styles.container, styles.centered]}>
        <Text style={styles.errorText}>Error: {error}</Text>
        <Text style={styles.errorHint}>Make sure the backend is running on http://localhost:8000</Text>
      </View>
    );
  }

  if (!conversation) {
    return (
      <View style={[styles.container, styles.centered]}>
        <Text style={styles.errorText}>No conversation loaded</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <ChatInterface 
        conversation={conversation}
        currentUserId="user-1"
        onSendMessage={(content) => sendMessage('user-1', content)}
        onEditMessage={updateMessage}
        onTagClick={(tag) => console.log('Navigate to wiki:', tag)}
      />
      <StatusBar style="auto" />
    </View>
  );
}

